export default {
  __name: 'socketTest',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const props = {
      pdfUrl: {
        type: String,
        default: "./IDKL.pdf",
        required: true
      },
      scale: {
        type: Number,
        default: 1.5
      }
    };
    const scrollContainer = ref(null);
    const pageCanvases = ref([]);
    const renderTasks = new Map(); // 用于跟踪渲染任务
    const pdfDoc = ref(null);
    const pageCount = ref(0);
    const totalHeight = ref(0);
    const pageHeights = ref([]);
    const pageOffsets = ref([]);
    const visiblePages = ref([]);
    const loading = ref(false);
    const viewportHeight = ref(0);
    const scrollPosition = ref(0);

    // PDF.js 配置
    pdfjsLib.GlobalWorkerOptions.workerSrc = 'https://cdn.jsdelivr.net/npm/pdfjs-dist@3.4.120/build/pdf.worker.min.js';

    // Canvas 引用设置
    const setCanvasRef = (el, pageNumber) => {
      if (el) {
        pageCanvases.value[pageNumber - 1] = el;
      }
    };

    // 加载PDF文档
    const loadPDF = async () => {
      loading.value = true;
      try {
        const loadingTask = pdfjsLib.getDocument(props.pdfUrl);
        pdfDoc.value = await loadingTask.promise;
        pageCount.value = pdfDoc.value.numPages;

        // 初始化数组
        pageCanvases.value = new Array(pageCount.value).fill(null);
        await calculatePageDimensions();
        updateVisiblePages();
      } catch (error) {
        console.error('PDF加载失败:', error);
      } finally {
        loading.value = false;
      }
    };

    // 计算页面尺寸
    const calculatePageDimensions = async () => {
      pageHeights.value = [];
      pageOffsets.value = [];
      let currentOffset = 0;
      for (let i = 1; i <= pageCount.value; i++) {
        const page = await pdfDoc.value.getPage(i);
        const viewport = page.getViewport({
          scale: props.scale
        });
        const height = viewport.height;
        pageHeights.value.push(height);
        pageOffsets.value.push(currentOffset);
        currentOffset += height;
      }
      totalHeight.value = currentOffset;
    };

    // 渲染页面（带取消功能）
    const renderPage = async pageNumber => {
      if (!pdfDoc.value || pageNumber < 1 || pageNumber > pageCount.value) return;

      // 取消之前的渲染任务
      if (renderTasks.has(pageNumber)) {
        const oldTask = renderTasks.get(pageNumber);
        oldTask.cancel();
        renderTasks.delete(pageNumber);
      }
      const page = await pdfDoc.value.getPage(pageNumber);
      const viewport = page.getViewport({
        scale: props.scale
      });
      const canvas = pageCanvases.value[pageNumber - 1];
      if (!canvas) return;

      // 创建新渲染任务
      const task = page.render({
        canvasContext: canvas.getContext('2d'),
        viewport: viewport
      });
      renderTasks.set(pageNumber, task);
      try {
        await task.promise;
      } catch (err) {
        if (err.name !== 'RenderingCancelledException') {
          console.error('页面渲染失败:', err);
        }
      } finally {
        renderTasks.delete(pageNumber);
      }
    };

    // 更新可见页面
    const updateVisiblePages = () => {
      if (!scrollContainer.value || pageHeights.value.length === 0) return;
      viewportHeight.value = scrollContainer.value.clientHeight;
      const scrollTop = scrollContainer.value.scrollTop;
      scrollPosition.value = scrollTop;
      const buffer = viewportHeight.value;
      const startY = Math.max(0, scrollTop - buffer);
      const endY = scrollTop + viewportHeight.value + buffer;
      const newVisiblePages = [];
      for (let i = 0; i < pageCount.value; i++) {
        const pageStart = pageOffsets.value[i];
        const pageEnd = pageStart + pageHeights.value[i];
        if (pageEnd > startY && pageStart < endY) {
          newVisiblePages.push({
            pageNumber: i + 1,
            offset: pageOffsets.value[i],
            height: pageHeights.value[i]
          });
        }
      }
      visiblePages.value = newVisiblePages;

      // 并行渲染可见页面
      visiblePages.value.forEach(page => {
        renderPage(page.pageNumber);
      });
    };

    // 滚动处理
    const handleScroll = () => {
      requestAnimationFrame(updateVisiblePages);
    };

    // 窗口大小变化处理
    const handleResize = () => {
      if (pdfDoc.value) {
        calculatePageDimensions().then(updateVisiblePages);
      }
    };

    // 组件卸载时清理
    onUnmounted(() => {
      // 取消所有渲染任务
      renderTasks.forEach(task => task.cancel());
      renderTasks.clear();

      // 销毁PDF文档
      if (pdfDoc.value) {
        pdfDoc.value.destroy();
        pdfDoc.value = null;
      }
    });
    onMounted(() => {
      loadPDF();
      window.addEventListener('resize', handleResize);
    });
    const __returned__ = {
      props,
      scrollContainer,
      pageCanvases,
      renderTasks,
      pdfDoc,
      pageCount,
      totalHeight,
      pageHeights,
      pageOffsets,
      visiblePages,
      loading,
      viewportHeight,
      scrollPosition,
      setCanvasRef,
      loadPDF,
      calculatePageDimensions,
      renderPage,
      updateVisiblePages,
      handleScroll,
      handleResize,
      ref,
      onMounted,
      onUnmounted,
      get pdfjsLib() {
        return pdfjsLib;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
